mapper.select:
select 

from t_task
where 1=1

    and projectId = #{projectid,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}

mapper.delete:
delete from t_task
where 
projectid = #{projectid,jdbcType=VARCHAR}
and
startuptime = #{startuptime,jdbcType=DATE}
and
endtime = #{endtime,jdbcType=DATE}
